import React, { useState, useEffect } from 'react';
import '../App.css';
//import firebase from './firebase';

import { initializeApp } from 'firebase/app';
import { getAuth, signInWithRedirect, GoogleAuthProvider, signOut } from 'firebase/auth';
import { useNavigate } from 'react-router-dom';

function App() {

	const [userId, setUserId] = useState('');
	const [username, setUsername] = useState('');
	const navigate = useNavigate();
	const firebaseConfig = {
		apiKey: "AIzaSyCqxHDg3mlIlUNokf8pd9VjAwAj9HgmOq4",
		authDomain: "wheeloffortune-login.firebaseapp.com",
		projectId: "wheeloffortune-login",
		storageBucket: "wheeloffortune-login.appspot.com",
		messagingSenderId: "5607927999",
		appId: "1:5607927999:web:0a67b3ffc5b342530f160a",
		measurementId: "G-KS9Z5VWJDD"
	};

	initializeApp(firebaseConfig);

	const signInWithGoogle = () => {
		const provider = new GoogleAuthProvider();
		const auth = getAuth();
		signInWithRedirect(auth, provider)
			.then((result) => {
				// User signed in
				const user = result.user;
				console.log(user);
				console.log(user.email);
				setUserId(user.email);
			}).catch((error) => {
				// Handle Errors here.
				console.error(error);
			});

	};

	const auth = getAuth();
	auth.onAuthStateChanged(user => {
		if (user) {
			// User is signed in.
			console.log("User is signed in:", user);

			// console.log(user.email)
			setUserId(user.email)
			const Id = user.email
			// console.log("00000", Id)
			// navigate('/game', { state: { Id } });
		} else {
			// No user is signed in.
			console.log("No user is signed in.");
		}
	});

	const signOutUser = () => {
		signOut(auth)
			.then(() => {
				// Sign-out successful.
				console.log('User signed out');
				setUserId("")
			})
			.catch((error) => {
				// An error happened.
				console.error('Sign-out error:', error);
			});
	};
	// const handleUsernameChange = (event) => {
	// 	setUsername(event.target.value);
	// 	console.log("username",event.target.value)
	// };
	const startGame = () => {
		console.log("username",username)
	};
	  // 处理输入框值变化的函数
	  const handleInputChange = (e) => {
		setUsername(e.target.value);
	  };


	return (
		<div style={{ textAlign: 'center', marginTop: '20px' }}>
			<img
				src="wheelOfFortune.png"
				alt="Description of the image"
				style={{ maxWidth: '100%', maxHeight: '400px', borderRadius: '10px' }}
			/>
			{/* <button onClick={signInWithGoogle}>Sign in with Google</button>
	  <button onClick={signOutUser}>Sign out</button>
      <p>{userId}</p> */}
			{userId ? (
				// 如果有用户已登录
				<div>
					<button onClick={signOutUser} style={{
						padding: '10px 20px',
						fontSize: '16px',
						backgroundColor: '#3498db',
						color: '#fff',
						border: 'none',
						borderRadius: '5px',
						cursor: 'pointer',
						marginTop: '30px'
					}}>Sign out</button>
					<p>Welcome, {userId}!</p>
				</div>
			) : (
				// 如果没有用户登录
				<div>
					<div>
						<button onClick={signInWithGoogle}
							style={{
								padding: '10px 20px',
								fontSize: '16px',
								backgroundColor: '#2ecc71',
								color: '#fff',
								border: 'none',
								borderRadius: '5px',
								cursor: 'pointer',
								marginTop: '10px'
							}}>Sign in with Google</button>
						<p>Please sign in to continue.</p>
					</div>

				</div>
			)}

			<div>
				<button onClick={startGame}
					style={{
						padding: '10px 20px',
						fontSize: '16px',
						backgroundColor: 'purple',
						color: '#fff',
						border: 'none',
						borderRadius: '5px',
						cursor: 'pointer',
						marginTop: '10px'
					}}>Start the Game</button>
			</div>

		</div>
	);
}

export default App;
