import React, { useState, useEffect } from 'react';
import '../App.css';
import { useGlobalContext } from '../components/GlobalContext';
import axios from 'axios'


function Scores() {
  //   const [inputValueFromForm, setInputValueFromForm] = useState('');
  const { googleID, updateGoogleID } = useGlobalContext();
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);



    const handleDelete = (id) => {
      console.log("delete",id)
    };
  const getUsername = async (googleId) => {
    try {
      const response = await axios.get('https://wheeloffortune-game.wl.r.appspot.com/user/findAllGameRecords');

      console.log(response.data);
      console.log(response.data[0].name)
      return response.data[0].name || '';
    } catch (error) {
      console.error('Error fetching username:', error);
      return '';
    }
  };
  const displayByGoogleId = () => {
    axios.get('https://wheeloffortune-game.wl.r.appspot.com/game/findByGoogleId', {
      params: {
        googleId: googleID
      }
    })
      .then(async (response) => {
        // 使用 Promise.all 来等待所有 getUsername 的 Promise 解决
        const usernames = await Promise.all(response.data.map((score) => getUsername(score.googleId)));

        // 将 usernames 与 scores 合并成对象数组
        const updatedRecords = response.data.map((score, index) => ({
          ...score,
          username: usernames[index],
        }));

        setRecords(updatedRecords);
        setLoading(false);
        console.log("updatedRecords",updatedRecords);
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });
  };
  useEffect(() => {
    googleID?updateGoogleID(googleID):updateGoogleID(localStorage.getItem("googleId"))
    displayByGoogleId();

}, []);

  return (
    <div className="Profile">

      <div>Profile</div>
      <div>{googleID}</div>
      <div>
      <h2>Records for {googleID}</h2>
      {loading ? (
          <p>Loading...</p>
        ) : (
          <table style={{ width: '80%', borderCollapse: 'collapse', marginTop: '20px' }}>
            <thead>
              <tr>
                <th style={{ textAlign: 'left' }}>username</th>
                <th style={{ textAlign: 'left' }}> Google ID</th>
                <th style={{ textAlign: 'left' }}>Score</th>
                <th style={{ textAlign: 'left' }}>Timestamp</th>
              </tr>
            </thead>
            <tbody>
              {records.map((score) => (
                <tr key={score.id}>
                  <td style={{ textAlign: 'left' }}>{score.username}</td>
                  <td style={{ textAlign: 'left' }}>{score.googleId}</td>
                  <td style={{ textAlign: 'left' }}>{score.score}</td>
                  <td style={{ textAlign: 'left' }}>{score.timestamp}</td>
                  <td style={{ textAlign: 'left' }}>
                  <button
                    style={{ color: 'red' }}
                    onClick={() => handleDelete(score.id)}
                  >
                    Delete
                  </button>
                </td>
                </tr>
              ))}
            </tbody>
          </table>
        )}


      {/* <table style={{ width: '80%', borderCollapse: 'collapse', marginTop: '20px' }}>
        <thead>
          <tr>
            <th style={{ textAlign: 'left' }}>username</th>
            <th style={{ textAlign: 'left' }}> Google ID</th>
            <th style={{ textAlign: 'left' }}>Score</th>
            <th style={{ textAlign: 'left' }}>Timestamp</th>
          </tr>
        </thead>
        <tbody>
          {records.map((score) => (
            <tr key={score.id}>
              <td style={{ textAlign: 'left' }}>{score.username}</td>
              <td style={{ textAlign: 'left' }}>{score.googleId}</td>
              <td style={{ textAlign: 'left' }}>{score.score}</td>
              <td style={{ textAlign: 'left' }}>{score.timestamp}</td>
            </tr>
          ))}
        </tbody>
      </table> */}
    </div>
    </div>
  );
}

export default Scores;
