import React, { useState, useEffect } from 'react';
import '../App.css';
import { useGlobalContext } from '../components/GlobalContext';
import axios from 'axios'


function Profile() {
  //   const [inputValueFromForm, setInputValueFromForm] = useState('');
  const { googleID, updateGoogleID } = useGlobalContext();
  const [records, setRecords] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);



  //   const handleFormSubmit = (value) => {
  //     setInputValueFromForm(value);
  //     processGuess(value)
  //   };
  const getUsername = async (googleId) => {
    try {
      const response = await axios.get('https://wheeloffortune-game.wl.r.appspot.com/user/findByGoogleId', {
        params: {
          googleId: googleID
        }
      });

      console.log(response.data);
      console.log(response.data[0].name)
      return response.data[0].name || '';
    } catch (error) {
      console.error('Error fetching username:', error);
      return '';
    }
  };
  // const getUsername = (googleId) => {
  //   axios.get('https://wheeloffortune-game.wl.r.appspot.com/user/findByGoogleId' + '?googleId=' + googleID)
  //   .then(response => {

  //     console.log(response.data)
  //     console.log(response.data[0].name)
  //     return response.data[0].name;
  //   })
  //   .catch(error => {
  //     setError(error.message);
  //     setLoading(false);
  //     return ""
  //   });
  // };
  function displayByGoogleId() {
    axios.get('https://wheeloffortune-game.wl.r.appspot.com/game/findByGoogleId' + '?googleId=' + googleID)
      .then(response => {
        setRecords(response.data);  // Axios packs the response in a 'data' property
        setLoading(false);
        console.log(response.data)
      })
      .catch(error => {
        setError(error.message);
        setLoading(false);
      });

  };
  useEffect(() => {
    displayByGoogleId();
}, []);

  return (
    <div className="Profile">

      <div>Profile</div>
      <div>{googleID}</div>
      <div>
      <h2>Records for {googleID}</h2>
      <table style={{ width: '80%', borderCollapse: 'collapse', marginTop: '20px' }}>
        <thead>
          <tr>
            <th style={{ textAlign: 'left' }}>username</th>
            <th style={{ textAlign: 'left' }}> Google ID</th>
            <th style={{ textAlign: 'left' }}>Score</th>
            <th style={{ textAlign: 'left' }}>Timestamp</th>
          </tr>
        </thead>
        <tbody>
          {records.map((score) => (
            <tr key={score.id}>
              <td style={{ textAlign: 'left' }}>{getUsername(score.googleId)}</td>
              <td style={{ textAlign: 'left' }}>{score.googleId}</td>
              <td style={{ textAlign: 'left' }}>{score.score}</td>
              <td style={{ textAlign: 'left' }}>{score.timestamp}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
    </div>
  );
}

export default Profile;
