import React, { useState, useEffect } from 'react';
import logo from './logo.svg';
import './App.css';
import Form from './Form'

function App() {
  const [inputValueFromForm, setInputValueFromForm] = useState('');
  const [info, setInfo] = useState('');
  const [hiddenPhrase, setHiddenPhrase] = useState('');

  const phrase = "hHappy coding"

  const handleFormSubmit = (value) => {
    // Receive the input value from MyForm and set it in App's state

    setInputValueFromForm(value);
    processGuess(value)
  };
  const getHiddenPhrase = (phrase) => {
    let hiddenPhrase = '';

    for (let i = 0; i < phrase.length; i++) {
      if (/[a-zA-Z]/.test(phrase[i])) {
        hiddenPhrase += '*';
      } else {
        hiddenPhrase += phrase[i];
      }
    }
    return hiddenPhrase;
  }
  const processGuess = (ch) => {
    if(hiddenPhrase==phrase){
      setInfo("Congratulations!");
      return;
    }
    console.log("ch",ch)
    if (/^[a-zA-Z]$/.test(ch)) {
      let flag = false;

      for (let i = 0; i < phrase.length; ++i) {
        const letter = phrase[i];
        if (letter === ch) {
          flag = true;
          let guessPhrase = hiddenPhrase;
          // guessPhrase[i] = ch;
          setHiddenPhrase(hiddenPhrase.substring(0, i) + ch + hiddenPhrase.substring(i + 1));
          // hiddenPhrase[i] = ch;
        }
      }

      if (!flag) {
        setInfo("It's wrong! Input again!")
      } else {
        setInfo("It's right. Input next guess")
      }
    } else {
      setInfo("It is not a letter!!!")
    }
  } 

const inialize = () => {
  setHiddenPhrase(getHiddenPhrase(phrase));

}
// Use the useEffect hook to call the function each time the component re-renders
useEffect(() => {
  inialize(); // Call the function
}, []);
return (
  <div className="App">
    <img
      src="wheelOfFortune.png"
      alt="Top Image"
      style={{
        height: '300px',
        width: '900px'
      }}
    />
    {/* <h1>WheelOfFortune</h1> */}
    <div style={{
      fontSize: '80px',
      marginTop: '40px'
    }}>{hiddenPhrase}</div>
    <div className="form-container">
      <Form onSubmit={handleFormSubmit} />
      <p>Input value from MyForm: {inputValueFromForm}</p>
      <h2>{info}</h2>
    </div>
  </div>
);
  }

export default App;
